@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/* Custom Gaming Theme Styles */
.gradient-primary {
  background: linear-gradient(135deg, hsl(217, 91%, 60%) 0%, hsl(261, 91%, 60%) 100%);
}

.glow {
  box-shadow: 0 0 20px hsl(217, 91%, 60% / 0.3);
}

.card-hover {
  transition: all 0.2s ease-in-out;
}

.card-hover:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px hsl(0, 0%, 0% / 0.3);
}

.game-card {
  background: linear-gradient(145deg, var(--card), hsl(222, 84%, 10%));
  border: 1px solid var(--border);
}

.level-badge {
  background: linear-gradient(135deg, hsl(142, 76%, 36%) 0%, hsl(142, 76%, 26%) 100%);
}

.navbar-blur {
  backdrop-filter: blur(10px);
  background: hsl(222, 84%, 5% / 0.8);
}

/* Gaming specific animations */
@keyframes pulse-glow {
  0%, 100% {
    box-shadow: 0 0 20px hsl(217, 91%, 60% / 0.3);
  }
  50% {
    box-shadow: 0 0 30px hsl(217, 91%, 60% / 0.5);
  }
}

.pulse-glow {
  animation: pulse-glow 2s ease-in-out infinite;
}

/* Scrollbar Styling */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: var(--muted);
}

::-webkit-scrollbar-thumb {
  background: var(--primary);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: hsl(217, 91%, 50%);
}

/* Line clamp utility */
.line-clamp-2 {
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

