import { useState, useEffect, useCallback } from 'react';
import { ref, push, onValue, query, orderByChild, equalTo, get } from 'firebase/database';
import { database } from '../lib/firebase';
import { ChatMessage, InsertChatMessage } from '@shared/schema';
import { useAuth } from '../contexts/AuthContext';

export function useChat(orderId: string) {
  const { currentUser } = useAuth();
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<Error | null>(null);

  useEffect(() => {
    if (!orderId) {
      setLoading(false);
      return;
    }

    const messagesRef = ref(database, 'chatMessages');
    const messagesQuery = query(messagesRef, orderByChild('orderId'), equalTo(orderId));

    const unsubscribe = onValue(
      messagesQuery,
      (snapshot) => {
        try {
          const messagesData: ChatMessage[] = [];

          if (snapshot.exists()) {
            snapshot.forEach((childSnapshot) => {
              const messageData = childSnapshot.val();
              messagesData.push({
                id: childSnapshot.key!,
                ...messageData,
                timestamp: new Date(messageData.timestamp)
              });
            });
          }

          // Sort messages by timestamp
          messagesData.sort((a, b) => a.timestamp.getTime() - b.timestamp.getTime());

          setMessages(messagesData);
          setError(null);
        } catch (err) {
          setError(err as Error);
        } finally {
          setLoading(false);
        }
      },
      (err) => {
        setError(err as Error);
        setLoading(false);
      }
    );

    return () => unsubscribe();
  }, [orderId]);

  const sendMessage = useCallback(async (content: string) => {
    if (!currentUser || !orderId || !content.trim()) {
      throw new Error('Missing required data for sending message');
    }

    // Content filtering for contact information
    const contactInfoPattern = /@|\.com|phone|discord|skype|telegram|whatsapp|email|gmail|yahoo|hotmail|\+\d|call|text|contact/i;
    const isFiltered = contactInfoPattern.test(content);

    const messageData: InsertChatMessage = {
      orderId,
      senderId: currentUser.uid,
      content: content.trim(),
      isFiltered,
      filteredReason: isFiltered ? 'Contact information detected' : undefined
    };

    const messagesRef = ref(database, 'chatMessages');
    await push(messagesRef, {
      ...messageData,
      timestamp: new Date().toISOString()
    });

    if (isFiltered) {
      throw new Error('Message contains contact information and has been flagged for review.');
    }
  }, [currentUser, orderId]);

  return {
    messages,
    loading,
    error,
    sendMessage
  };
}

export function useChatParticipants(orderId: string) {
  const [participants, setParticipants] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchParticipants = async () => {
      if (!orderId) {
        setLoading(false);
        return;
      }

      try {
        // Fetch order to get buyer and seller IDs
        const orderRef = ref(database, `orders/${orderId}`);
        const orderSnapshot = await get(orderRef);

        if (!orderSnapshot.exists()) {
          setLoading(false);
          return;
        }

        const orderData = orderSnapshot.val();
        const participantIds = [orderData.buyerId, orderData.sellerId];

        // Fetch participant details
        const participantPromises = participantIds.map(async (id) => {
          const userRef = ref(database, `users/${id}`);
          const userSnapshot = await get(userRef);

          if (userSnapshot.exists()) {
            return {
              id,
              ...userSnapshot.val()
            };
          }
          return null;
        });

        const participantData = await Promise.all(participantPromises);
        setParticipants(participantData.filter(Boolean));
      } catch (error) {
        console.error('Error fetching chat participants:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchParticipants();
  }, [orderId]);

  return { participants, loading };
}

client/src/hooks/useFirestore.ts.content:
import { useState, useEffect } from 'react';
import { ref, onValue, off, get, set, update, push, remove } from 'firebase/database';
import { database } from '../lib/firebase';

export interface FirestoreHookOptions {
  listen?: boolean;
}

export function useFirestoreDocument<T>(path: string, options: FirestoreHookOptions = {}) {
  const [data, setData] = useState<T | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<Error | null>(null);

  useEffect(() => {
    if (!path) {
      setLoading(false);
      return;
    }

    const dbRef = ref(database, path);

    if (options.listen) {
      const unsubscribe = onValue(
        dbRef,
        (snapshot) => {
          try {
            if (snapshot.exists()) {
              const value = snapshot.val();
              setData(value);
            } else {
              setData(null);
            }
            setError(null);
          } catch (err) {
            setError(err as Error);
          } finally {
            setLoading(false);
          }
        },
        (err) => {
          setError(err as Error);
          setLoading(false);
        }
      );

      return () => unsubscribe();
    } else {
      get(dbRef)
        .then((snapshot) => {
          if (snapshot.exists()) {
            setData(snapshot.val());
          } else {
            setData(null);
          }
          setError(null);
        })
        .catch((err) => {
          setError(err as Error);
        })
        .finally(() => {
          setLoading(false);
        });
    }
  }, [path, options.listen]);

  return { data, loading, error };
}

export function useFirestoreCollection<T>(path: string, options: FirestoreHookOptions = {}) {
  const [data, setData] = useState<Record<string, T>>({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<Error | null>(null);

  useEffect(() => {
    if (!path) {
      setLoading(false);
      return;
    }

    const dbRef = ref(database, path);

    if (options.listen) {
      const unsubscribe = onValue(
        dbRef,
        (snapshot) => {
          try {
            if (snapshot.exists()) {
              const value = snapshot.val();
              setData(value || {});
            } else {
              setData({});
            }
            setError(null);
          } catch (err) {
            setError(err as Error);
          } finally {
            setLoading(false);
          }
        },
        (err) => {
          setError(err as Error);
          setLoading(false);
        }
      );

      return () => unsubscribe();
    } else {
      get(dbRef)
        .then((snapshot) => {
          if (snapshot.exists()) {
            setData(snapshot.val() || {});
          } else {
            setData({});
          }
          setError(null);
        })
        .catch((err) => {
          setError(err as Error);
        })
        .finally(() => {
          setLoading(false);
        });
    }
  }, [path, options.listen]);

  // Convert to array format
  const dataArray = Object.entries(data).map(([id, value]) => ({
    id,
    ...value
  })) as (T & { id: string })[];

  return { data: dataArray, dataObject: data, loading, error };
}

export const useFirestoreActions = () => {
  const createDocument = async (path: string, data: any) => {
    const dbRef = ref(database, path);
    return set(dbRef, data);
  };

  const updateDocument = async (path: string, data: any) => {
    const dbRef = ref(database, path);
    return update(dbRef, data);
  };

  const deleteDocument = async (path: string) => {
    const dbRef = ref(database, path);
    return remove(dbRef);
  };

  const pushDocument = async (path: string, data: any) => {
    const dbRef = ref(database, path);
    return push(dbRef, data);
  };

  return {
    createDocument,
    updateDocument,
    deleteDocument,
    pushDocument
  };
};

